li.al:
de Bank:
              de Sociale Verzekeringsbank, bedoeld in artikel 1, onderdeel
                c, van de Organisatiewet sociale verzekeringen 1997;

partner:
              de persoon, bedoeld in artikel 1, eerste lid, onder g, dan wel
                artikel 1, tweede lid, van de wet;

gewezen partner:
              degene die aanspraak heeft op een uitkering als bedoeld in
                artikel 5, eerste of tweede lid, van de wet;

kind:
              het kind, bedoeld in artikel 1, eerste lid, onder h, dan wel
                artikel 1, derde lid, van de wet;

bewijsstukken met betrekking tot door de remigrant of zijn
                    partner ontvangen uitkeringen welke op de
                    remigratie-uitkering in mindering worden gebracht;

bewijsstukken met betrekking tot door de gewezen partner
                    ontvangen uitkeringen welke op de remigratie-uitkering in
                    mindering worden gebracht;
